{% block extra_scripts %}
<script>
// Load system statistics
async function loadStats() {
    try {
        const response = await fetch('/memory/health');
        const data = await response.json();
        
        document.getElementById('chat-count').textContent = data.chat_count || 0;
        document.getElementById('system-status').textContent = data.status === 'healthy' ? '✅ Healthy' : '❌ Error';
    } catch (error) {
        document.getElementById('chat-count').textContent = 'Error';
        document.getElementById('system-status').textContent = '❌ Error';
    }
}

// Load stats when page loads
document.addEventListener('DOMContentLoaded', loadStats);
</script>
{% endblock %}
