So the end of your config should look like:
nginx    # Dynamic Memory Search API endpoints
    location /api/ {
        proxy_pass http://<redacted.ip>:40004/api/;
        proxy_set_header Host $host;
        proxy_set_header X-Forwarded-For $remote_addr;
        proxy_set_header X-Forwarded-Proto https;
        proxy_buffering off;
        proxy_redirect off;
        
        # Force JSON content type
        proxy_hide_header Content-Type;
        add_header Content-Type "application/json; charset=utf-8" always;
        
        # CORS headers
        add_header Access-Control-Allow-Origin "*" always;
        add_header Access-Control-Allow-Methods "GET, OPTIONS" always;
        add_header Access-Control-Allow-Headers "Content-Type" always;
    }
    
    # Memory System HTML endpoints for ChatGPT
    location /mem/ {
        proxy_pass http://<redacted.ip>:40001/mem/;
        proxy_set_header Host $host;
        proxy_set_header X-Forwarded-For $remote_addr;
        proxy_set_header X-Forwarded-Proto https;
        proxy_buffering off;
        proxy_redirect off;
        
        # Keep HTML content type
        add_header Access-Control-Allow-Origin "*" always;
        add_header Access-Control-Allow-Methods "GET, OPTIONS" always;
        add_header Access-Control-Allow-Headers "Content-Type" always;
    }
}
